&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;
&lt;span class=&#34;line&#34;&gt;&lt;span style=&#34;color:#59873A;--shiki-dark:#80A665&#34;&gt;	fprintf&lt;/span&gt;&lt;span style=&#34;color:#1e754f;--shiki-dark:#4d9375&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt;stderr&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;,&lt;/span&gt;&lt;span style=&#34;color:#B5695977;--shiki-dark:#C98A7D77&#34;&gt; &#34;&lt;/span&gt;&lt;span style=&#34;color:#B56959;--shiki-dark:#C98A7D&#34;&gt;Now, we overwrite the first 8 bytes of the data at &lt;/span&gt;&lt;span style=&#34;color:#A65E2B;--shiki-dark:#C99076&#34;&gt;%p&lt;/span&gt;&lt;span style=&#34;color:#B56959;--shiki-dark:#C98A7D&#34;&gt; to point right before the 0x20.&lt;/span&gt;&lt;span style=&#34;color:#A65E2B;--shiki-dark:#C99076&#34;&gt;\n&lt;/span&gt;&lt;span style=&#34;color:#B5695977;--shiki-dark:#C98A7D77&#34;&gt;&#34;&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;,&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt; a&lt;/span&gt;&lt;span style=&#34;color:#1e754f;--shiki-dark:#4d9375&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;;&lt;/span&gt;&lt;/span&gt;
&lt;span class=&#34;line&#34;&gt;&lt;span style=&#34;color:#AB5959;--shiki-dark:#CB7676&#34;&gt;	*&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt;d &lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;=&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt; &lt;/span&gt;&lt;span style=&#34;color:#1e754f;--shiki-dark:#4d9375&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#AB5959;--shiki-dark:#CB7676&#34;&gt;unsigned&lt;/span&gt;&lt;span style=&#34;color:#AB5959;--shiki-dark:#CB7676&#34;&gt; long&lt;/span&gt;&lt;span style=&#34;color:#AB5959;--shiki-dark:#CB7676&#34;&gt; long&lt;/span&gt;&lt;span style=&#34;color:#1e754f;--shiki-dark:#4d9375&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt; &lt;/span&gt;&lt;span style=&#34;color:#1e754f;--shiki-dark:#4d9375&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#a65e2b;--shiki-dark:#d4976c&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#a13865;--shiki-dark:#d9739f&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#AB5959;--shiki-dark:#CB7676&#34;&gt;char*&lt;/span&gt;&lt;span style=&#34;color:#a13865;--shiki-dark:#d9739f&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#AB5959;--shiki-dark:#CB7676&#34;&gt;&amp;#x26;&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt;stack_var&lt;/span&gt;&lt;span style=&#34;color:#a65e2b;--shiki-dark:#d4976c&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#AB5959;--shiki-dark:#CB7676&#34;&gt; -&lt;/span&gt;&lt;span style=&#34;color:#AB5959;--shiki-dark:#CB7676&#34;&gt; sizeof&lt;/span&gt;&lt;span style=&#34;color:#a65e2b;--shiki-dark:#d4976c&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt;d&lt;/span&gt;&lt;span style=&#34;color:#a65e2b;--shiki-dark:#d4976c&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#1e754f;--shiki-dark:#4d9375&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;;&lt;/span&gt;&lt;/span&gt;
&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;
&lt;span class=&#34;line&#34;&gt;&lt;span style=&#34;color:#59873A;--shiki-dark:#80A665&#34;&gt;	fprintf&lt;/span&gt;&lt;span style=&#34;color:#1e754f;--shiki-dark:#4d9375&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt;stderr&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;,&lt;/span&gt;&lt;span style=&#34;color:#B5695977;--shiki-dark:#C98A7D77&#34;&gt; &#34;&lt;/span&gt;&lt;span style=&#34;color:#B56959;--shiki-dark:#C98A7D&#34;&gt;3rd malloc(8): &lt;/span&gt;&lt;span style=&#34;color:#A65E2B;--shiki-dark:#C99076&#34;&gt;%p&lt;/span&gt;&lt;span style=&#34;color:#B56959;--shiki-dark:#C98A7D&#34;&gt;, putting the stack address on the free list&lt;/span&gt;&lt;span style=&#34;color:#A65E2B;--shiki-dark:#C99076&#34;&gt;\n&lt;/span&gt;&lt;span style=&#34;color:#B5695977;--shiki-dark:#C98A7D77&#34;&gt;&#34;&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;,&lt;/span&gt;&lt;span style=&#34;color:#59873A;--shiki-dark:#80A665&#34;&gt; malloc&lt;/span&gt;&lt;span style=&#34;color:#a65e2b;--shiki-dark:#d4976c&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#2F798A;--shiki-dark:#4C9A91&#34;&gt;8&lt;/span&gt;&lt;span style=&#34;color:#a65e2b;--shiki-dark:#d4976c&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#1e754f;--shiki-dark:#4d9375&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;;&lt;/span&gt;&lt;/span&gt;
&lt;span class=&#34;line&#34;&gt;&lt;span style=&#34;color:#59873A;--shiki-dark:#80A665&#34;&gt;	fprintf&lt;/span&gt;&lt;span style=&#34;color:#1e754f;--shiki-dark:#4d9375&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt;stderr&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;,&lt;/span&gt;&lt;span style=&#34;color:#B5695977;--shiki-dark:#C98A7D77&#34;&gt; &#34;&lt;/span&gt;&lt;span style=&#34;color:#B56959;--shiki-dark:#C98A7D&#34;&gt;4th malloc(8): &lt;/span&gt;&lt;span style=&#34;color:#A65E2B;--shiki-dark:#C99076&#34;&gt;%p\n&lt;/span&gt;&lt;span style=&#34;color:#B5695977;--shiki-dark:#C98A7D77&#34;&gt;&#34;&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;,&lt;/span&gt;&lt;span style=&#34;color:#59873A;--shiki-dark:#80A665&#34;&gt; malloc&lt;/span&gt;&lt;span style=&#34;color:#a65e2b;--shiki-dark:#d4976c&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#2F798A;--shiki-dark:#4C9A91&#34;&gt;8&lt;/span&gt;&lt;span style=&#34;color:#a65e2b;--shiki-dark:#d4976c&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#1e754f;--shiki-dark:#4d9375&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;;&lt;/span&gt;&lt;/span&gt;
&lt;span class=&#34;line&#34;&gt;&lt;span style=&#34;color:#2993a3;--shiki-dark:#5eaab5&#34;&gt;&amp;#125;&lt;/span&gt;&lt;/span&gt;
&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;
&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;初步看源码大概了解了程序的运行，不过对于48行的sizeof(d)存有疑问，大小是多少(这里应该是一个指针的大小即8字节)，这里是将d的地址直接改为了fake_chunk的地址（也就是改了a的地址），但是在fastbin链表的fd指针的值也被这种方式修改了吗&lt;/p&gt;
&lt;p&gt;后面反应过来，malloc返回的指针是数据段的位置而不是&lt;code&gt;pre_size&lt;/code&gt;，所以直接修改&lt;code&gt;*d&lt;/code&gt;等于修改的是对应chunk的fd（修改的是*d指向地址内的值）&lt;/p&gt;
&lt;h2 id=&#34;2调试程序-3&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#2调试程序-3&#34;&gt;#&lt;/a&gt; 2.调试程序：&lt;/h2&gt;
&lt;h3 id=&#34;1执行第9行unsigned-long-long-stack_var&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#1执行第9行unsigned-long-long-stack_var&#34;&gt;#&lt;/a&gt; 1.执行第9行&lt;code&gt;unsigned long long stack_var;&lt;/code&gt;&lt;/h3&gt;
&lt;p&gt;查看此处定义的栈参数的地址&lt;/p&gt;
&lt;p&gt;这个栈地址是&lt;code&gt;0x7fffffffdcb0&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;&lt;img loading=&#34;lazy&#34; src=&#34;https://vvwwv.oss-cn-nanjing.aliyuncs.com/ctfshow/TBOELRRCM4%29OHKJVBGD8IU9.png&#34; alt=&#34;&#34; /&gt;&lt;/p&gt;
&lt;h3 id=&#34;2执行到第17行&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#2执行到第17行&#34;&gt;#&lt;/a&gt; 2.执行到第17行&lt;/h3&gt;
&lt;pre class=&#34;shiki shiki-themes vitesse-light vitesse-dark&#34; style=&#34;background-color:#ffffff;--shiki-dark-bg:#121212;color:#393a34;--shiki-dark:#dbd7caee&#34; tabindex=&#34;0&#34;&gt;&lt;code class=&#34;language-c&#34;&gt;&lt;span class=&#34;line&#34;&gt;&lt;span style=&#34;color:#AB5959;--shiki-dark:#CB7676&#34;&gt;int&lt;/span&gt;&lt;span style=&#34;color:#AB5959;--shiki-dark:#CB7676&#34;&gt; *&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt;a &lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;=&lt;/span&gt;&lt;span style=&#34;color:#59873A;--shiki-dark:#80A665&#34;&gt; malloc&lt;/span&gt;&lt;span style=&#34;color:#2993a3;--shiki-dark:#5eaab5&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#2F798A;--shiki-dark:#4C9A91&#34;&gt;8&lt;/span&gt;&lt;span style=&#34;color:#2993a3;--shiki-dark:#5eaab5&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;;&lt;/span&gt;&lt;/span&gt;
&lt;span class=&#34;line&#34;&gt;&lt;span style=&#34;color:#AB5959;--shiki-dark:#CB7676&#34;&gt;int&lt;/span&gt;&lt;span style=&#34;color:#AB5959;--shiki-dark:#CB7676&#34;&gt; *&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt;b &lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;=&lt;/span&gt;&lt;span style=&#34;color:#59873A;--shiki-dark:#80A665&#34;&gt; malloc&lt;/span&gt;&lt;span style=&#34;color:#2993a3;--shiki-dark:#5eaab5&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#2F798A;--shiki-dark:#4C9A91&#34;&gt;8&lt;/span&gt;&lt;span style=&#34;color:#2993a3;--shiki-dark:#5eaab5&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;;&lt;/span&gt;&lt;/span&gt;
&lt;span class=&#34;line&#34;&gt;&lt;span style=&#34;color:#AB5959;--shiki-dark:#CB7676&#34;&gt;int&lt;/span&gt;&lt;span style=&#34;color:#AB5959;--shiki-dark:#CB7676&#34;&gt; *&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt;c &lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;=&lt;/span&gt;&lt;span style=&#34;color:#59873A;--shiki-dark:#80A665&#34;&gt; malloc&lt;/span&gt;&lt;span style=&#34;color:#2993a3;--shiki-dark:#5eaab5&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#2F798A;--shiki-dark:#4C9A91&#34;&gt;8&lt;/span&gt;&lt;span style=&#34;color:#2993a3;--shiki-dark:#5eaab5&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;;&lt;/span&gt;&lt;/span&gt;
&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;申请了3个chunk，查看堆情况&lt;/p&gt;
&lt;p&gt;&lt;img loading=&#34;lazy&#34; src=&#34;https://vvwwv.oss-cn-nanjing.aliyuncs.com/ctfshow/%5DM2OFN3A%29%60%40%60PMHMA%40QTV_N.png&#34; alt=&#34;&#34; /&gt;&lt;/p&gt;
&lt;h3 id=&#34;3执行23行freea&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#3执行23行freea&#34;&gt;#&lt;/a&gt; 3.执行23行&lt;code&gt;free(a)&lt;/code&gt;&lt;/h3&gt;
&lt;p&gt;查看堆，a已经进入了&lt;code&gt;fastbin&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;&lt;img loading=&#34;lazy&#34; src=&#34;https://vvwwv.oss-cn-nanjing.aliyuncs.com/ctfshow/%7EDGKT%7DW%25DAJ%5D%5B%607OJ%7E5%60%7EZU.png&#34; alt=&#34;&#34; /&gt;&lt;/p&gt;
&lt;h3 id=&#34;4执行29行freeb&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#4执行29行freeb&#34;&gt;#&lt;/a&gt; 4.执行29行&lt;code&gt;free(b)&lt;/code&gt;&lt;/h3&gt;
&lt;p&gt;释放b是为了绕过检测，使chunk a不是于fastbin直接相连的chunk，这样第二次释放就不会被检测出来&lt;/p&gt;
&lt;p&gt;&lt;img loading=&#34;lazy&#34; src=&#34;https://vvwwv.oss-cn-nanjing.aliyuncs.com/ctfshow/YF%40%7DLVK%29C6MQKL%40V%40N%25%7DL%5D2.png&#34; alt=&#34;&#34; /&gt;&lt;/p&gt;
&lt;h3 id=&#34;5执行32行freea&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#5执行32行freea&#34;&gt;#&lt;/a&gt; 5.执行32行&lt;code&gt;free(a)&lt;/code&gt;&lt;/h3&gt;
&lt;p&gt;对a进行再次释放（&lt;code&gt;double free&lt;/code&gt;）&lt;/p&gt;
&lt;p&gt;在fastbin中已经变成了 &lt;code&gt;fastbin-&amp;gt;chunk a-&amp;gt;chunk b-&amp;gt;chunk a&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;&lt;img loading=&#34;lazy&#34; src=&#34;https://vvwwv.oss-cn-nanjing.aliyuncs.com/ctfshow/U0U12EX%25JZMM%5BPSBK4B_4Z6.png&#34; alt=&#34;&#34; /&gt;&lt;/p&gt;
&lt;h3 id=&#34;6执行36行unsigned-long-long-d-malloc8&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#6执行36行unsigned-long-long-d-malloc8&#34;&gt;#&lt;/a&gt; 6.执行36行&lt;code&gt;unsigned long long *d = malloc(8);&lt;/code&gt;&lt;/h3&gt;
&lt;p&gt;此时d申请的chunk会是chunk a，此时&lt;code&gt;*d=*a&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;&lt;img loading=&#34;lazy&#34; src=&#34;https://vvwwv.oss-cn-nanjing.aliyuncs.com/ctfshow/IK6Q%25I951SIM9%7E_R0X%7EG4%24N.png&#34; alt=&#34;&#34; /&gt;&lt;/p&gt;
&lt;p&gt;打印指针d (发现是是&lt;code&gt;chunk a&lt;/code&gt;的fd位置的地址)&lt;/p&gt;
&lt;p&gt;&lt;img loading=&#34;lazy&#34; src=&#34;https://vvwwv.oss-cn-nanjing.aliyuncs.com/ctfshow/%29Y%40H5NSC0YX%28%5B_L%29%5BAT5HB3.png&#34; alt=&#34;&#34; /&gt;&lt;/p&gt;
&lt;h3 id=&#34;7执行39行malloc8&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#7执行39行malloc8&#34;&gt;#&lt;/a&gt; 7.执行39行&lt;code&gt;malloc(8)&lt;/code&gt;&lt;/h3&gt;
&lt;p&gt;此时剩下的&lt;code&gt;chunk a&lt;/code&gt;是第一次释放的（不是第二次释放的）&lt;/p&gt;
&lt;p&gt;&lt;img loading=&#34;lazy&#34; src=&#34;https://vvwwv.oss-cn-nanjing.aliyuncs.com/ctfshow/T9JQ%25TA%402DSNPCY%28Q%7DXEQ%25W.png&#34; alt=&#34;&#34; /&gt;&lt;/p&gt;
&lt;h3 id=&#34;8执行45行stack_var-0x20&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#8执行45行stack_var-0x20&#34;&gt;#&lt;/a&gt; 8.执行45行&lt;code&gt;	stack_var = 0x20;&lt;/code&gt;&lt;/h3&gt;
&lt;p&gt;改变栈的值=0x20，为了构造&lt;code&gt;fake_chunk&lt;/code&gt;，因为这里要作为fake_chunk的size位，那么我们就需要&lt;code&gt;fastbin&lt;/code&gt;中的&lt;code&gt;chunk&lt;/code&gt;的fd指向&lt;code&gt;pre_size&lt;/code&gt;的位置，也就是&lt;code&gt;&amp;amp;stack_var-8&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;查看stack_var的地址&lt;code&gt;0x7fffffffdcb0&lt;/code&gt; ，所以要将其&lt;code&gt;0x7fffffffdca8&lt;/code&gt;作为&lt;code&gt;chunk a &lt;/code&gt;的fd位的值&lt;/p&gt;
&lt;p&gt;&lt;img loading=&#34;lazy&#34; src=&#34;https://vvwwv.oss-cn-nanjing.aliyuncs.com/ctfshow/9JTX0QQF9G%7E%7BP%40%5B2AN5%7EJ%29D.png&#34; alt=&#34;&#34; /&gt;&lt;/p&gt;
&lt;h3 id=&#34;9执行48行d-unsigned-long-long-charstack_var-sizeofd&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#9执行48行d-unsigned-long-long-charstack_var-sizeofd&#34;&gt;#&lt;/a&gt; 9.执行48行&lt;code&gt;*d = (unsigned long long) (((char*)&amp;amp;stack_var) - sizeof(d));&lt;/code&gt;&lt;/h3&gt;
&lt;p&gt;这里修改了d指针内存放的值，改为了&lt;code&gt;stack_var地址-0x8&lt;/code&gt;，（sizeof(d)计算指针的大小为8字节）&lt;/p&gt;
&lt;p&gt;&lt;img loading=&#34;lazy&#34; src=&#34;https://vvwwv.oss-cn-nanjing.aliyuncs.com/ctfshow/%5DV00%5D5856NVLE%5B6%7D2ORVU4P.png&#34; alt=&#34;&#34; /&gt;&lt;/p&gt;
&lt;p&gt;此时还可以发现fastbin中fake_chunk指向了chunk a 的fd处，但是这时可以通过下面发现&lt;code&gt;0x555555759018&lt;/code&gt;的地方为0，修改的话也可以作为一个chunk来使用&lt;/p&gt;
&lt;h3 id=&#34;10执行50行malloc8&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#10执行50行malloc8&#34;&gt;#&lt;/a&gt; 10.执行50行&lt;code&gt;malloc(8)&lt;/code&gt;&lt;/h3&gt;
&lt;p&gt;这时会将chunk a申请出去，然后fastbin指向fake_chunk&lt;/p&gt;
&lt;p&gt;可以看到abc三个chunk都已经不在fastbin中了,fasstbin指向了fake_chunk&lt;/p&gt;
&lt;p&gt;&lt;img loading=&#34;lazy&#34; src=&#34;https://vvwwv.oss-cn-nanjing.aliyuncs.com/ctfshow/LL%5DNTD%7B%5BFMGYT%5B%5B%7BB%5BQWII3.png&#34; alt=&#34;&#34; /&gt;&lt;/p&gt;
&lt;h3 id=&#34;11执行51行malloc8&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#11执行51行malloc8&#34;&gt;#&lt;/a&gt; 11.执行51行&lt;code&gt;malloc(8)&lt;/code&gt;&lt;/h3&gt;
&lt;p&gt;这时就会将我们伪造出来的fake_chunk申请出来，可以通过程序的读写功能进行修改，这里的fake_chunk是在栈上，也就是说我们其实通过这种方式最终将栈空间申请了出来进行改写&lt;/p&gt;
&lt;p&gt;&lt;img loading=&#34;lazy&#34; src=&#34;https://vvwwv.oss-cn-nanjing.aliyuncs.com/ctfshow/R%7BNHL%7DIH%29I%24C6U3ZZMQ68BU.png&#34; alt=&#34;&#34; /&gt;&lt;/p&gt;
&lt;p&gt;此时我们就得到了一个伪造在栈空间的&lt;code&gt;chunk&lt;/code&gt;&lt;/p&gt;
&lt;h3 id=&#34;12程序运行结果&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#12程序运行结果&#34;&gt;#&lt;/a&gt; 12.程序运行结果&lt;/h3&gt;
&lt;p&gt;&lt;img loading=&#34;lazy&#34; src=&#34;https://vvwwv.oss-cn-nanjing.aliyuncs.com/ctfshow/IJ17PBK%25%5DUN94D%29NN%25MT5FD.png&#34; alt=&#34;&#34; /&gt;&lt;/p&gt;
&lt;p&gt;发现了通过这种double free就可以申请一个栈空间来进行改写，只不过需要在栈上先伪造一个size位符合大小，才能用这种方式&lt;/p&gt;
&lt;h1 id=&#34;4house_of_einherjaroff-by-one利用可进行向后合并&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#4house_of_einherjaroff-by-one利用可进行向后合并&#34;&gt;#&lt;/a&gt; 4.&lt;code&gt;house_of_einherjar&lt;/code&gt;（off by one利用，可进行向后合并）&lt;/h1&gt;
&lt;p&gt;利用了&lt;code&gt;off by one &lt;/code&gt;漏洞&lt;s&gt;不仅可以修改下一个堆块的 &lt;code&gt;prev_size&lt;/code&gt;&lt;/s&gt;，还可以修改下一个堆块的 PREV_INUSE 比特位，通过这个方式可以进行向后合并操作（需要绕过unlink检测），通过这个将我们构造的任意地方的fake_chunk申请回来进行利用&lt;/p&gt;
&lt;p&gt;free 函数(向后合并,4002行)【向后合并其实是低地址的早就是空闲的chunk与高地址的chunk合并，p指针指向低地址】：&lt;/p&gt;
&lt;pre class=&#34;shiki shiki-themes vitesse-light vitesse-dark&#34; style=&#34;background-color:#ffffff;--shiki-dark-bg:#121212;color:#393a34;--shiki-dark:#dbd7caee&#34; tabindex=&#34;0&#34;&gt;&lt;code class=&#34;language-c&#34;&gt;&lt;span class=&#34;line&#34;&gt;&lt;span style=&#34;color:#A0ADA0;--shiki-dark:#758575DD&#34;&gt;        /* consolidate backward */&lt;/span&gt;&lt;/span&gt;
&lt;span class=&#34;line&#34;&gt;&lt;span style=&#34;color:#1E754F;--shiki-dark:#4D9375&#34;&gt;        if&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt; &lt;/span&gt;&lt;span style=&#34;color:#2993a3;--shiki-dark:#5eaab5&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#AB5959;--shiki-dark:#CB7676&#34;&gt;!&lt;/span&gt;&lt;span style=&#34;color:#59873A;--shiki-dark:#80A665&#34;&gt;prev_inuse&lt;/span&gt;&lt;span style=&#34;color:#1e754f;--shiki-dark:#4d9375&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt;p&lt;/span&gt;&lt;span style=&#34;color:#1e754f;--shiki-dark:#4d9375&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#2993a3;--shiki-dark:#5eaab5&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt; &lt;/span&gt;&lt;span style=&#34;color:#2993a3;--shiki-dark:#5eaab5&#34;&gt;&amp;#123;&lt;/span&gt;&lt;span style=&#34;color:#A0ADA0;--shiki-dark:#758575DD&#34;&gt; //检测p位是否为0&lt;/span&gt;&lt;/span&gt;
&lt;span class=&#34;line&#34;&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt;            prevsize &lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;=&lt;/span&gt;&lt;span style=&#34;color:#59873A;--shiki-dark:#80A665&#34;&gt; prev_size&lt;/span&gt;&lt;span style=&#34;color:#1e754f;--shiki-dark:#4d9375&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt;p&lt;/span&gt;&lt;span style=&#34;color:#1e754f;--shiki-dark:#4d9375&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;;&lt;/span&gt;&lt;/span&gt;
&lt;span class=&#34;line&#34;&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt;            size &lt;/span&gt;&lt;span style=&#34;color:#AB5959;--shiki-dark:#CB7676&#34;&gt;+=&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt; prevsize&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;;&lt;/span&gt;&lt;/span&gt;
&lt;span class=&#34;line&#34;&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt;            p &lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;=&lt;/span&gt;&lt;span style=&#34;color:#59873A;--shiki-dark:#80A665&#34;&gt; chunk_at_offset&lt;/span&gt;&lt;span style=&#34;color:#1e754f;--shiki-dark:#4d9375&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt;p&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;,&lt;/span&gt;&lt;span style=&#34;color:#AB5959;--shiki-dark:#CB7676&#34;&gt; -&lt;/span&gt;&lt;span style=&#34;color:#a65e2b;--shiki-dark:#d4976c&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#a13865;--shiki-dark:#d9739f&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#AB5959;--shiki-dark:#CB7676&#34;&gt;long&lt;/span&gt;&lt;span style=&#34;color:#a13865;--shiki-dark:#d9739f&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt; prevsize&lt;/span&gt;&lt;span style=&#34;color:#a65e2b;--shiki-dark:#d4976c&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#1e754f;--shiki-dark:#4d9375&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;;&lt;/span&gt;&lt;/span&gt;
&lt;span class=&#34;line&#34;&gt;&lt;span style=&#34;color:#59873A;--shiki-dark:#80A665&#34;&gt;            unlink&lt;/span&gt;&lt;span style=&#34;color:#1e754f;--shiki-dark:#4d9375&#34;&gt;(&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt;av&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;,&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt; p&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;,&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt; bck&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;,&lt;/span&gt;&lt;span style=&#34;color:#393A34;--shiki-dark:#DBD7CAEE&#34;&gt; fwd&lt;/span&gt;&lt;span style=&#34;color:#1e754f;--shiki-dark:#4d9375&#34;&gt;)&lt;/span&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;;&lt;/span&gt;&lt;span style=&#34;color:#A0ADA0;--shiki-dark:#758575DD&#34;&gt; //这里会触发unlink&lt;/span&gt;&lt;/span&gt;
&lt;span class=&#34;line&#34;&gt;&lt;span style=&#34;color:#999999;--shiki-dark:#666666&#34;&gt;        &lt;/span&gt;&lt;span style=&#34;color:#2993a3;--shiki-dark:#5eaab5&#34;&gt;&amp;#125;&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;
